    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        // Initialize views
        editTextAmount = findViewById(R.id.editTextAmount);
        spinnerFromCurrency = findViewById(R.id.spinnerFromCurrency);
        spinnerToCurrency = findViewById(R.id.spinnerToCurrency);
        buttonConvert = findViewById(R.id.buttonConvert);
        textViewResult = findViewById(R.id.textViewResult);
        exitButton = findViewById(R.id.buttonexit);


        // Set up spinners
        ArrayAdapter<String> currencyAdapter = new ArrayAdapter<>(this, android.R.layout.simple_spinner_item, currencyNames);
        currencyAdapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
        spinnerFromCurrency.setAdapter(currencyAdapter);
        spinnerToCurrency.setAdapter(currencyAdapter);

        // Set up button click listener
        buttonConvert.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                convertCurrency();
            }
        });
        exitButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                finish(); // Close the activity and exit the app
            }
        });




    }


    private void convertCurrency() {
        String amountString = editTextAmount.getText().toString();
        if (amountString.isEmpty()) {
            Toast.makeText(this, "Please enter an amount", Toast.LENGTH_SHORT).show();
            return;
        }

        double amount;
        try {
            amount = Double.parseDouble(amountString);
        } catch (NumberFormatException e) {
            Toast.makeText(this, "Invalid amount entered", Toast.LENGTH_SHORT).show();
            return;
        }

        int fromCurrencyIndex = spinnerFromCurrency.getSelectedItemPosition();
        int toCurrencyIndex = spinnerToCurrency.getSelectedItemPosition();

        if (fromCurrencyIndex == toCurrencyIndex) {
            Toast.makeText(this, "Please select different currencies", Toast.LENGTH_SHORT).show();
            return;
        }

        double[] fromRates = conversionRates[fromCurrencyIndex];
        double toRate = fromRates[toCurrencyIndex];


        if (toRate == 0) {
            Toast.makeText(this, "Conversion rate not available", Toast.LENGTH_SHORT).show();
            return;
        }

        double result = amount * toRate;

        DecimalFormat decimalFormat = new DecimalFormat("#,##0.00");
        String resultString = decimalFormat.format(result);

        String fromCurrencyName = currencyNames[toCurrencyIndex];
        String toCurrencyName = currencyNames[fromCurrencyIndex];

        textViewResult.setText("Result: " + resultString + " " + toCurrencyName);
    }


